mapper.insert:
INSERT INTO board(
	subject
	, writer
	, contents
	, ip
	, hit
	, reg_date
	, mod_date
) VALUES (
	#{subject}
	, #{writer}
	, #{contents}
	, #{ip}
	, 0
	, now()
	, now()
)

mapper.update:
UPDATE board SET 
	subject=#{subject}
	, writer=#{writer}
	, contents=#{contents}
	, ip=#{ip}
	, mod_date=now()
WHERE 
	num=#{num}

