mapper.insert:
INSERT INTO board(
	subject
	, writer
	, contents
	, ip
	, hit
	, reg_date
	, mod_date
	, comment_count
) VALUES (
	#{subject}
	, #{writer}
	, #{contents}
	, #{ip}
	, 0
	, now()
	, now()
	, 0
)

mapper.update:
UPDATE board SET 
	subject=#{subject}
	, writer=#{writer}
	, contents=#{contents}
	, ip=#{ip}
	, mod_date=now()
WHERE 
	num=#{num}

mapper.insert:
INSERT INTO COMMENT(
	  comment_writer
	, comment_content
	, write_date
	, linked_article_num
) VALUES (
	  #{comment_writer}
	, #{comment_content}
	, NOW()
	, #{linked_article_num}
)

INSERT INTO FILE(
	  file_name
	, origin_file_name
	, file_size
	, article_num
) VALUES (
	  #{file_name}
	, #{origin_file_name}
	, #{file_size}
	, (SELECT MAX(num) FROM board)
)
